function createUserTeacher(firstName, lastName) {
  let userName = createErnestLluchTeacherUsername(firstName, lastName);
  let password = ERNEST_LLUCH_DEFAULT_TEACHER_PASSWORD;
  let orgUnitPath = ORG_UNIT_TEACHERS;
  if (addUserServiceAgent(firstName, lastName, userName, password, orgUnitPath) == false) return false;
  return true;
}

function createGroup(groupName) {
  let email = createErnestLluchGroupEmail(groupName);
  if (createGroupServiceAgent(groupName, email) == false) return false;
  return true;
}

function addGroupMemberWithGroupName(groupName, userEmail) {
  let group = getGroupServiceAgent(groupName, DOMINI);
  if (group == false) return false;
  let user = getUserServiceAgent(userEmail);
  if (user == false) return false;
  if (!addGroupMemberServiceAgent(group.email, userEmail)) return false;
  return true;
}

function deleteGroupMemberWithGroupNameAndMemberEmail(groupName, userEmail) {
  let group = getGroupServiceAgent(groupName, DOMINI);
  if (group == false) return false;
  let user = getUserServiceAgent(userEmail);
  if (user == false) return false;
  if (!deleteGroupMemberServiceAgent(group.email, userEmail)) return false;
  return true;  
}

function deleteGroupWithGroupName(groupName) {
  let group = getGroupServiceAgent(groupName, DOMINI);
  if (group == false) return false; 
  if (!deleteGroupServiceAgent(group.id)) return false;
  return true;
}

function deleteUserTeacher(userEmail) {
  let user = getUserServiceAgent(userEmail);
  if (user == false) return false;
  if (deleteUserServiceAgent(user.id) == false) return false;
  return true;
}

function createSharedDrive(driveName) {
  if (createSharedDriveServiceAgent(driveName) == false) return false;
  return true;
}

function getSharedDrive(driveName) {
  let sharedDrive = getSharedDriveServiceAgent(driveName);
  if (sharedDrive == false) return false;
  return sharedDrive;
}

function deleteSharedDrive(driveName) {
  let sharedDrive = getSharedDriveServiceAgent(driveName);
  if (sharedDrive == false) return false;
  if (deleteDriveServiceAgent(sharedDrive.id) == false) return false;
  return true;
}

function setPermissionsOrganizerToUserInDrive(driveName, userEmail) {
  let sharedDrive = getSharedDriveServiceAgent(driveName);
  if (sharedDrive == false) return false;
  if (setPermissionsToElementServiceAgent(sharedDrive.id, userEmail, DriveRoles.ORGANIZER) == false) return false;
  return true;
}

function setPermissionsWriterToUserInDrive(driveName, userEmail) {
  let sharedDrive = getSharedDriveServiceAgent(driveName);
  if (sharedDrive == false) return false;
  if (setPermissionsToElementServiceAgent(sharedDrive.id, userEmail, DriveRoles.WRITER) == false) return false;
  return true;}

function setPermissionsReaderToUserInDrive(driveName, userEmail) {
  let sharedDrive = getSharedDriveServiceAgent(driveName);
  if (sharedDrive == false) return false;
  if (setPermissionsToElementServiceAgent(sharedDrive.id, userEmail, DriveRoles.READER) == false) return false;
  return true;}

function getPermissionForUserInDrive(driveName, emailAddress) {
  let sharedDrive = getSharedDrive(driveName);
  if (sharedDrive == false) return false;
  let permissions = listPermissionsInElementServiceAgent(sharedDrive.id);
  for (let i=0; i<permissions.items.length; i++) {
    let permissionEmailAddress = permissions.items[i].emailAddress;
    if (permissionEmailAddress == emailAddress) return permissions.items[i];
  }
  return false;
}

function deletePermissionForUserInDrive(driveName, emailAddress) {
  let sharedDrive = getSharedDrive(driveName);
  if (sharedDrive == false) return false;
  let permission = getPermissionForUserInDrive(sharedDrive.name, emailAddress);
  if (permission == false) return false;
  if (deletePermissionInElementServiceAgent(sharedDrive.id, permission.id) == false) return false;
  return true;
}

function createFolderInSharedDrive(driveName, folderName) {
  let sharedDrive = getSharedDriveServiceAgent(driveName);
  if (sharedDrive == false) return false;
  return createFolderServiceAgent(folderName, sharedDrive.id);
}  

function createFolderInFolder(parentFolderName, folderName) {
  let parentFolder = getFolderServiceAgent(parentFolderName);
  if (parentFolder == null) return null;
  return createFolderServiceAgent(folderName, parentFolder.id);
}

function getFolder(folderName) {
  let folder = getFolderServiceAgent(folderName);
  if (folder == false) return false;
  return folder;
}

function deleteFolder(folderName) {
  let folder = getFolderServiceAgent(folderName);
  if (folder == false) return false;
  if (deleteFolderServiceAgent(folder.id) == false) return false;
  return true;
}

function setPermissionsOrganizerToUserInFolder(folderName, userEmail) {
  let folder = getFolderServiceAgent(folderName);
  if (folder == false) return false;
  if (setPermissionsToElementServiceAgent(folder.id, userEmail, FileRoles.ORGANIZER) == false) return false;
  return true;
}

function setPermissionsWriterToUserInFolder(folderName, userEmail) {
  let folder = getFolderServiceAgent(folderName);
  if (folder == false) return false;
  if (setPermissionsToElementServiceAgent(folder.id, userEmail, FileRoles.WRITER) == false) return false;
  return true;
}

function setPermissionsReaderToUserInFolder(folderName, userEmail) {
  let folder = getFolderServiceAgent(folderName);
  if (folder == false) return false;
  if (setPermissionsToElementServiceAgent(folder.id, userEmail, FileRoles.READER) == false) return false;
  return true;
}

function getPermissionForUserInFolder(folderName, emailAddress) {
  let folder = getFolderServiceAgent(folderName);
  if (folder == false) return false;
  let permissions = listPermissionsInElementServiceAgent(folder.id);
  for (let i=0; i<permissions.items.length; i++) {
    let permissionEmailAddress = permissions.items[i].emailAddress;
    if (permissionEmailAddress == emailAddress) return permissions.items[i];
  }
  return false;
}

function deletePermissionForUserInFolder(folderName, emailAddress) {
  let folder = getFolderServiceAgent(folderName);
  if (folder == false) return false;
  let permission = getPermissionForUserInFolder(folder.title, emailAddress);
  if (permission == false) return false;
  if (deletePermissionInElementServiceAgent(folder.id, permission.id) == false) return false;
  return true;
}

function sheetExists(sheetname){    
  ss = getCurrentSpreadsheet();
  if (ss.getSheetByName(sheetname)!= null) return true;
  return false;
}

function printAlertInSpreadsheet(text) {
  SpreadsheetApp.getUi().alert(text);
}

function getDataInSheet(sheetName) {
  ss = getCurrentSpreadsheet();
  if (!sheetExists(sheetName))  {
    printAlertInSpreadsheet("Sheet " + sheetName + " not found");
    return false;
    }
  sheet = ss.getSheetByName(sheetName);
  return sheet.getDataRange().getValues();
}

// USERS
//
// https://developers.google.com/apps-script/advanced/admin-sdk-directory
// - GET USER
// - CREATE USER
// - DELETE USER

function getUserServiceAgent(userEmail) {
  try {
    const user = AdminDirectory.Users.get(userEmail);
    return user;
  } catch (err) {
    Logger.log(JSON.stringify(err));
    return false;
  }
}

function addUserServiceAgent(firstName, lastName, userName, password, orgUnitPath=null) {
  let user = {
    primaryEmail: userName,
    name: {
      givenName: firstName,
      familyName: lastName,
    },
    password: password,
    orgUnitPath: orgUnitPath,
  };
  try {
    return AdminDirectory.Users.insert(user);
  } catch (err) {
    Logger.log(JSON.stringify(err));
    return false;
  }
}

function deleteUserServiceAgent(userId) {
  try {
    AdminDirectory.Users.remove(userId);
    return true;
  } catch (err) {
    Logger.log(JSON.stringify(err));
    return false;
  }
}

// GROUPS
//
// https://developers.google.com/apps-script/advanced/admin-sdk-directory
// https://developers.google.com/admin-sdk/directory/reference/rest
// - Create group
// - Get group
// - Add member
// - Delete member
// - Delete group

function createGroupServiceAgent(groupName, groupEmail) {
  let group = {
  "email": groupEmail,
  "name": groupName,
  "adminCreated": true,
}
  try {
    return AdminDirectory.Groups.insert(group);
  } catch(err) {
    Logger.log(JSON.stringify(err));
    return false;
  }
}

function deleteGroupServiceAgent(groupId) {
  try {
    AdminDirectory.Groups.remove(groupId);
    return true;
  } catch(err) {
    Logger.log(JSON.stringify(err));
    return false;
  }
}

function getGroupServiceAgent(groupName, domain) {
  let query = "name='" + groupName + "'";
  let optionalArgs = {
    domain: domain,
    maxResults: 1,
    query: query,
    viewType: 'domain_public',
    projection: 'full'
  }
  try {
    var groups = AdminDirectory.Groups.list(optionalArgs).groups;
  } 
  catch(err) {
    Logger.log(JSON.stringify(err));
  }
  if (groups && groups.length > 0) return groups[0];
  return false;
}

function addGroupMemberServiceAgent(groupEmail, userEmail) {
  const member = {
    email: userEmail,
    role: 'MEMBER'
  };
  try {
    AdminDirectory.Members.insert(member, groupEmail);
    return true;
  } catch (err) {
    Logger.log(JSON.stringify(err));
    return false;
  }
}

function deleteGroupMemberServiceAgent(groupEmail, userId) {
  try {
    AdminDirectory.Members.remove(groupEmail, userId);
    return true;
  } catch(err) {
    Logger.log(JSON.stringify(err));
    return false;
  }
}

// SHARED DRIVES
//
// - CREATE SHARED DRIVE
// - GET SHARED DRIVE

function createSharedDriveServiceAgent(driveName) {
  let sharedDrive = getSharedDriveServiceAgent(driveName);
  if (sharedDrive != false) return false;
  var requestID = generateUUID();
  var resource = {
    name: driveName
  };
  try {
    return Drive.Drives.insert(resource, requestID);
  }
  catch (err) {
    Logger.log(JSON.stringify(err));
    return false
  }
}

function getSharedDriveServiceAgent(driveName) {
  var query = 'name = "' + driveName + '"';
  var optionalArgs = { 
    q: query
  };
  try {
    var drivesList = Drive.Drives.list(optionalArgs);
  }
  catch (err) {
    Logger.log(JSON.stringify(err));
    return false
  }
  if (drivesList && drivesList.items.length > 0) return drivesList.items[0];
  return false;
}

function deleteDriveServiceAgent(driveId) {
  try {
    Drive.Drives.remove(driveId);
    return true;
  }
  catch (err) {
    Logger.log(JSON.stringify(err));
    return false;
  }
}

// FOLDERS
//
// - CREATE FOLDER
// - GET FOLDER

function createFolderServiceAgent(folderName, parentObjectId) {
  var resource = {
    title: folderName,
    mimeType: "application/vnd.google-apps.folder",
    parents:[{
      "id": parentObjectId,
    }]
  };
  var optionalArgs = {
    supportsAllDrives: true
    };
  try {
    return Drive.Files.insert(resource, null, optionalArgs);
  }  
  catch (err) {
    Logger.log(JSON.stringify(err));
    return false
  }
}

function getFolderServiceAgent(folderName) {
  var query = 'mimeType = "application/vnd.google-apps.folder" AND title = "' + folderName + '"';
  var optionalArgs = { 
    q: query,
    supportsAllDrives: true,
    corpora: "allDrives",
    includeItemsFromAllDrives : true
  };
  try {
    var drivesList = Drive.Files.list(optionalArgs);
    if (drivesList.items.length > 0) return drivesList.items[0];
    return false;
  }
  catch (err) {
    Logger.log(JSON.stringify(err));
    return false
  }
}

function deleteFolderServiceAgent(folderId) {
  try {
    var optionalArgs = { 
      supportsAllDrives: true,
      corpora: "allDrives",
      includeItemsFromAllDrives : true
    };
    Drive.Files.remove(folderId, optionalArgs);
    return true;
  }
  catch (err) {
    Logger.log(JSON.stringify(err));
    return false;
  }
}

// PERMISSIONS
//
// - LIST PERMISSIONS IN ELEMENT
// - SET PERMISSIONS TO ELEMENT
// - DELETE PERMISSIONS IN ELEMENT

function listPermissionsInElementServiceAgent(elementId) {
  var optionalArgs = { 
    supportsAllDrives: true
    };
  try {
    return Drive.Permissions.list(elementId, optionalArgs);
    }
  catch (err) {
    Logger.log(JSON.stringify(err));
    return false
  }
}

function setPermissionsToElementServiceAgent(elementId, userEmail, role) {
  var resource = {
    role: role,
    type: "user",
    value: userEmail,
  }
  // optional arguments to work on Shared drive
  var optionalArgs = {
    sendNotificationEmails: false,
    supportsAllDrives: true
  };
  try {
    Drive.Permissions.insert(resource, elementId, optionalArgs);
    return true;
  }
  catch (err) {
    Logger.log(JSON.stringify(err));
    return false
  }
}

function deletePermissionInElementServiceAgent(elementId, permissionId) {
  var optionalArgs = { 
    supportsAllDrives: true
    };
  try {
    Drive.Permissions.remove(elementId, permissionId, optionalArgs);
    return true;
  }
  catch (err) {
    Logger.log(JSON.stringify(err));
    return false
  }
}

function getCurrentSpreadsheet() {
  return SpreadsheetApp.getActiveSpreadsheet();
}

function getSpreadsheet(spreadsheetId) {
  return SpreadsheetApp.openById(spreadsheetId);
}

function variableInEnum(variable, myEnum) {
  for (var value in myEnum) {
    enumValue = myEnum[value];
    if (variable == enumValue) return true;
  }
  return false;
}

function generateUUID() {
  return Utilities.getUuid();
}

function createErnestLluchGroupEmail(groupName) {
  return stringConverter(groupName) + DOMINI_CORREU_PROFESSORS;
}

function createErnestLluchTeacherUsername(firstName, lastName) {
  return (stringConverter(firstName + lastName) + DOMINI_CORREU_PROFESSORS).toLowerCase();
}

function createErnestLluchStudentUsername(firstName, lastName, userType) {
  return (stringConverter(firstName + lastName) + DOMINI_CORREU_ALUMNES).toLowerCase();

}

function replaceAccents(textWithAccents) {
  return textWithAccents.normalize("NFD").replace(/[\u0300-\u036f]/g, "");
}

function stringConverter(textToConvert) {
    return replaceAccents(textToConvert.replace(/\s+/g, '_')
                        .toLowerCase());
}

const DriveRoles = {
  ORGANIZER: 'organizer',
  WRITER: 'writer',
  READER: 'reader',
}

const FileRoles = {
  ORGANIZER: 'fileOrganizer',
  WRITER: 'writer',
  READER: 'reader',
}

const ErnestLluchUserTypes = {
  TEACHER: 'teacher',
  STUDENT: 'student',
}
const DOMINI = "iernestlluch.cat";
const DOMINI_CORREU_ALUMNES = (Math.floor(Math.random() * 20) + 1).toString() + '@iernestlluch.cat';
const DOMINI_CORREU_PROFESSORS = '@iernestlluch.cat';
const ORG_UNIT_TEACHERS = "/Personal educatiu";
const ORG_UNIT_NEW_STUDENTS = "/Alumnes/Alumnes nous 2023-24";
const ERNEST_LLUCH_DEFAULT_TEACHER_PASSWORD = "3rn3st_Lluch"
const ERNEST_LLUCH_DEFAULT_STUDENT_PASSWORD = "Ernest_1234"
